import React from 'react';
import 'bootstrap/dist/css/bootstrap.css.map'
import './App.css';
import {Col, Container, Row} from "react-bootstrap";

function App() {

    //Creating a date instance
    const date = new Date();

    function today() {
        let weekday = new Array(7);
        weekday[0] = "Sun";
        weekday[1] = "Mon";
        weekday[2] = "Tue";
        weekday[3] = "Wed";
        weekday[4] = "Thurs";
        weekday[5] = "Fri";
        weekday[6] = "Sat";
        return weekday[date.getDay()]
    }

    const todayis = today();

    const weekDays = ['Sun ', 'Mon ', 'Tue ', 'Wed ', 'Thurs ', 'Fri ']

    function activeDay(today, weekDay) {
        console.log(today);
        console.log(weekDay);
        if (today === weekDay) {
            console.log('plop')
        } else {
            console.log(
                'fuck off'
            )
        }
    }

    activeDay(todayis, weekDays);
    //Setting the time to a 12hr clock with seconds
    const now = date.toLocaleString('en-Uk', {hour: 'numeric', minute: 'numeric', second: 'numeric', hour12: true})

    return (
        <Container>
            <Row>
                <Col>1 of 2</Col>
                <Col>2 of 2</Col>
            </Row>
            <Row>
                <Col>1 of 3</Col>
                <Col>2 of 3</Col>
                <Col>3 of 3</Col>
            </Row>
        </Container>
    );
}

//TOODO = style clock, add Temperature in users locale, add greeting depending on the time of day


export default App;
