import React from 'react';
import 'bootstrap/dist/css/bootstrap.css.map'
import './App.css';
import {Container, Col, Row} from "react-bootstrap";

function App() {

    //Creating a date instance
    const date = new Date();


    function colorToday(color) {
        var dayContainers = Array.prototype.slice.call(document.querySelectorAll('div.days p'), 0),
            days = ['Sun ', 'Mon ', 'Tue ', 'Wed ', 'Thurs ', 'Fri ', 'Sat ' ],
            day = new Date().getDay();
        dayContainers.forEach(function(d, i) {
            if (d.textContent.trim() === days[day]) {
                d.parentNode.style.backgroundColor = color;
            }
        });
    }
    colorToday('#fff');
    //Setting the time to a 12hr clock with seconds
    const now = date.toLocaleString('en-Uk', {hour: 'numeric', minute: 'numeric', second: 'numeric', hour12: true})

    return (
        <Container>
            <Row>
                <Col xs={12} id="root" className="clock">
                    <Col xs={12} className="numbers">
                        {now}
                    </Col>
                    <Col xs={12} className="days">
                        <p>Tue Wed</p>
                    </Col>
                </Col>
            </Row>
        </Container>

    );
}

//TOODO = style clock, add Temperature in users locale, add greeting depending on the time of day


export default App;
