import React from 'react';
import 'bootstrap/dist/css/bootstrap.css.map'
import './App.css';
import {Col, Container, Row} from "react-bootstrap";

function App() {

    //Creating a date instance
    const date = new Date();

    function today() {
        let weekday = new Array(7);
        weekday[0] = "Sun";
        weekday[1] = "Mon";
        weekday[2] = "Tue";
        weekday[3] = "Wed";
        weekday[4] = "Thurs";
        weekday[5] = "Fri";
        weekday[6] = "Sat";
        return weekday[date.getDay()]
    }

    const todayis = today();

    const weekDays = ['Sun ', 'Mon ', 'Tue ', 'Wed ', 'Thurs ', 'Fri ']

    function activeDay(today, weekDay) {
        console.log(today);
        console.log(weekDay);
        if (today === weekDay) {
            console.log('plop')
        } else {
            console.log(
                'fuck off'
            )
        }
    }

    activeDay(todayis, weekDays);
    //Setting the time to a 12hr clock with seconds
    const now = date.toLocaleString('en-Uk', {hour: 'numeric', minute: 'numeric', second: 'numeric', hour12: true})

    return (
        <Container>
            <Row>
                <div id="root" className="clock">
                    <Col xs={12} className="numbers">
                        {now}
                    </Col>
                </div>
            </Row>
            <Row className="daysHolder">
                <Col>
                    <p>Sun </p>
                </Col>
                <Col>
                    <p>Mon </p>
                </Col>
                <Col>
                    <p>Tue </p>
                </Col>
                <Col>
                    <p>Wed</p>
                </Col>
                <Col>
                    <p>Thu</p>
                </Col>
                <Col>
                    <p>Fri</p>
                </Col>
                <Col>
                    <p>Sat</p>
                </Col>
            </Row>
        </Container>

    );
}

//TOODO = style clock, add Temperature in users locale, add greeting depending on the time of day


export default App;
