import React from 'react';
// import logo from './logo.svg';
// import './App.css';

function App() {

    const date = new Date();
    const hours = date.getHours();
    const now = date.toLocaleString('en-Uk', { hour: 'numeric', minute: 'numeric', hour12: true })

    return (
    <div id="root">
      <h1>{now}</h1>
    </div>
  );
}



export default App;
